 */
public class Switch_statement_revised {

    static int menu() {
        int choice = 0;

        try {
            Scanner myScan = new Scanner(System.in);

            System.out.print("Menu\n"
                    + "1. Pick Larry\n"
                    + "2. Pick Curly\n"
                    + "3. Pick Moe\n"
                    + "Please pick 1,2 or 3:  ");
