    }

    static void switch_example1() {
        // this format is the same in most languages
        // and in all version of java

        int choice = -1;

        System.out.println("Example 1");
        choice = menu();

        switch (choice) {
            case 1:
                Larry();
                break;
            case 2:
                Curly();
                break;
            case 3:
                Moe();
                break;
