        System.out.println("\n--------------------------\n");
    }

    static void switch_example2() {
        // This format of a switch effective version 14+

        int choice = -1;

        System.out.println("Example 1");
        choice = menu();

        switch (choice) {
            case 1 -> Larry();
            case 2 -> Curly();
            case 3 -> Moe();
            default ->
                System.out.println("That wasn't a choice");

        }

        System.out.println("\n--------------------------\n");
    }

    public static void main(String[] args) {

        switch_example1();

