def factorial(n):
    if n == 0:
        # This is the base case n == 0
        return 1
    else:
        return n * factorial(n - 1)

def main():

    num = int(input("Enter a whole number: "))

    ans = factorial(num)

    # note that we can use {} with the .format as placeholders
    print("The factorial of {} is {}".format(num,ans))

