def sumlist(numberList):

    if len(numberList) == 1:
        return numberList[0]
    else:
        return numberList[0] + \
               sumlist(numberList[1:])

def main():

    theList = [1,3,5,7,9]

    ans = sumlist(theList)

    print("The sum of {} is {}".format(theList,ans))

main()
