Created on Mon Oct 2019

@author: jgoudy
"""

class car:



    carspeed = 0
    carengineOn = True


    # constructor  - there can only be one in python
    def __init__(self, carmake="",carmodel="",caryear="", carcolor=""):
        self.carspeed = 0
        self.carengineOn = False
        self.carmake = carmake
        self.carmodel = carmodel
        self.caryear= caryear
        self.carcolor = carcolor

    # __str__  defines what print() or str() is printed for the obje
    # example print(car) = what is defined here is what will be printed
    def __str__(self):
        return ("Make: " + self.carmake + "\n" + \
                "Model: " + self.carmodel + "\n" + \
                "Year: " + self.caryear + "\n" + \
                "Color: " + self.carcolor + "\n")


    # __str__ must return string object 
    # whereas __repr__ can return any python expression.
    #https://docs.python.org/3/reference/datamodel.html
    # when possible it should return a string

    def __repr__(self):
        return (self.carmake+","+ self.carmodel+","+ self.caryear+","+ self.carcolor)


    @property
    def make(self):
        return self.carmake    
    @make.setter
    def make(self, value):
        self.carmake = value

    @property
    def model(self):
        return self.carmodel    
    @model.setter
    def model(self, value):
        self.carmodel = value

    @property
    def year(self):
        return self.caryear    
    @year.setter
    def year(self, value):
        self.caryear = value

    @property
    def color(self):
        return self.carcolor    
    @color.setter
    def color(self, value):
        self.carcolor = value

    @property
    def speed(self):
        return self.carspeed

    @property
    def engineOn(self):
        return self.carengineOn

    def startCar(self):
        self.carengineOn = True

    def stopCar(self):
        self.carengineOn = False


    def accelerate(self,speed = 5):
        if (self.carengineOn == True) :
            self.carspeed += speed
            print("aaargh")
        else:
            print("Start the car")


    def deccelerate(self,speed = 5):
        if self.carspeed > 0:
            self.carspeed -= speed
        else:
            self.carspeed = 0
        print("car is stopped")


    def honk(self):
        print("beep beep")
# --------------------------------------------------        

def main():

   mycar1 = car()

   mycar2 = car("Ford", "Mustang","1967","red")

   mycar2.accelerate()
   mycar2.startCar()
   mycar2.accelerate()
   print(mycar2.speed)
   mycar2.accelerate()
   print(mycar2.speed)
   mycar2.accelerate(50)
   print(mycar2.speed)
   mycar2.deccelerate(70)

   print("The color of my car is " + mycar2.color)
   mycar2.color = "blue"
   print("The color of my car is " + mycar2.color)

   mycar1.honk()

   print("\bye bye")

main()
